def group_make_ph(array) #by peter, features hashes
  # array.shuffle!
  population = array.length
  group_count = population/4
  group_list = Hash.new([])

  # group_count.times do |k|
  #   group_list[k] = []
  # end

  population.times do |i|
    # print "#{i} #{array[i]} \n"
    group_list[ i%group_count ] += [array[i]]
  end

  return group_list
end

def account_group_generator_bf(names, group_size) #by brandi
  groups = Array.new(names.shuffle.each_slice(group_size).to_a)
  return groups
end


def acc_sort_as(names)#by andrew
    new_list = names.shuffle
    # p new_list.count
    min_group = 3
    max_group = 4

    if new_list.length % max_group == 0
      new_list.each_slice(4).to_a
    elsif names.length % min_group == 0
      new_list.each_slice(3).to_a
    elsif
      new_list.length % min_group <= 2
        acc_array = new_list.each_slice(3).to_a
        last_array = acc_array.pop
        for i in (0...last_array.count)
          acc_array[i] << last_array[i]
        end
        acc_array
    end
end

